package com.meng.mlog.common.result.helper;import com.meng.mlog.common.result.model.ResultVO;import com.meng.mlog.common.result.enums.ResultCodeEnum;public class ResultHelper {    /**     * 判断数据库操作是否成功     *     * @param result 数据库操作返回影响条数     * @return boolean     */    public static boolean returnBool(Integer result) {        return result != null && result > 0;    }    public static ResultVO success() {        return createSuccessResult(null);    }    public static <T> ResultVO<T> success(T data) {        return createSuccessResult(data);    }    private static <T> ResultVO<T> createSuccessResult(T data) {        return new ResultVO<T>()                .code(ResultCodeEnum.SUCCESS.code())                .message(ResultCodeEnum.SUCCESS.message())                .data(data);    }    public static ResultVO failure() {        return failure(ResultCodeEnum.FAILED);    }    public static ResultVO failure(ResultCodeEnum resultCodeEnum) {        return createFailureResult(resultCodeEnum.code(), resultCodeEnum.message());    }    public static ResultVO failure(String code, String message) {        return createFailureResult(code, message);    }    public static ResultVO failure(ResultCodeEnum resultCodeEnum, String message) {        return createFailureResult(resultCodeEnum.code(), message);    }    private static ResultVO createFailureResult(String code, String message) {        return new ResultVO()                .code(ResultCodeEnum.FAILED.code())                .message(ResultCodeEnum.FAILED.message());    }}